import React, { Component } from "react";
import { Link } from "react-router-dom";
import { MDBBtn } from "mdbreact";
import axios from "axios";
class Inovicesdata extends Component {
    constructor(props) {
        super(props);
        this.state={
            message:''
        };

        console.log("this.props", this.props.obj);
    }
    // delete=(e) => {
