    render() {
        console.log(this.props)
        let { obj } = this.props
        return (
            <tr>
                <td> {obj.slagme}</td>
                <td>{obj.c_name}</td>
                <td>{obj.c_address}</td>
                <td>{obj.number}</td>
                <td>{obj.date}</td>
                <td>{obj.services}</td>
                <td>{obj.items}</td>
                <td>{obj.quantity}</td>
                <td>{obj.total_amount}</td>
                <td>
                    <Link to={"/edit/" + obj.id} >
                        <button className="btn btn-primary btn-sm">
                            <i className="fas fa-edit">
                            </i>
                        </button>
                    </Link>
                    <MDBBtn size="sm" color="danger" >
                        <i className="fas fa-trash">
                        </i>
                    </MDBBtn>
                </td>
            </tr>
        )
    }
}
export default Inovicesdata;
