class ItemsDetail extends Component{
constructor(props){
    super(props);
    this.state={
       item:[]
    }
}

componentWillMount() {

    axios.get('http://localhost:5000/brp/oneitemdata/' +this.props.match.params.slagme)
        .then(res =>{
         this.setState({item:res.data})
            console.log(this.state.item)
        })
        .catch(error => {console.log(error)})
}

    render() {
        return(
            <MDBTable>
