import React,{ Component } from "react";
import axios from 'axios';
import {MDBBtn, MDBCard, MDBCardBody, MDBIcon, MDBTable, MDBTableBody, MDBTableHead, MDBTooltip} from "mdbreact";

class ItemsDetail extends Component{
constructor(props){
    super(props);
    this.state={
       item:[]
    }
}

componentWillMount() {

    axios.get('http://localhost:5000/brp/oneitemdata/' +this.props.match.params.slagme)
        .then(res =>{
         this.setState({item:res.data})
            console.log(this.state.item)
        })
        .catch(error => {console.log(error)})
}

    render() {
        return(
            <MDBCard style={{ width: "22rem" }}>
                <MDBCardBody>
                    <MDBTable  responsive striped hover bordered>
                        <MDBTableHead>
                            <tr>
                                <th>Item_detail</th>
                                <th>Qunatity</th>
                                <th>price</th>
                            </tr>
                        </MDBTableHead>
                        <MDBTableBody>
                            {this.state.item.map((items,i) =>{
                                return(
                                    <tr key={i}>
                                        <td>{items.detail}</td>
                                        <td>{items.qty}</td>
                                        <td>{items.price}</td>

                                    </tr>

                                )  })}
                        </MDBTableBody>
                    </MDBTable>
                </MDBCardBody>
            </MDBCard>



        )
    }
}
export default ItemsDetail;
